pragma solidity ^0.5.0;
pragma experimental ABIEncoderV2;

import "../interfaces/EIP1154.sol";
import "../libs/IexecODBLibCore.sol";

import "../IexecStore.sol";

contract IexecAccessors is IOracle // make interface
{
	function viewAccount     (address _user                   ) external view returns (IexecODBLibCore.Account memory);
	function viewPresigned   (bytes32 _id                     ) external view returns (bool);
	function viewConsumed    (bytes32 _id                     ) external view returns (uint256);
	function viewRequestDeals(bytes32 _id                     ) external view returns (bytes32[] memory);
	function viewDeal        (bytes32 _id                     ) external view returns (IexecODBLibCore.Deal memory);
	function viewTask        (bytes32 _taskid                 ) external view returns (IexecODBLibCore.Task memory);
	function viewContribution(bytes32 _taskid, address _worker) external view returns (IexecODBLibCore.Contribution memory);
	function viewScore       (address _worker                 ) external view returns (uint256);
}

contract IexecAccessorsDelegate is IexecAccessors, IexecStore
{
	function viewAccount(address _user)
	external view returns (IexecODBLibCore.Account memory)
	{
		return m_accounts[_user];
	}

	function viewPresigned(bytes32 _id)
	external view returns (bool)
	{
		return m_presigned[_id];
	}

	function viewConsumed(bytes32 _id)
	external view returns (uint256)
	{
		return m_consumed[_id];
	}

	function viewRequestDeals(bytes32 _id)
	external view returns (bytes32[] memory)
	{
		return m_requestdeals[_id];
	}

	function viewDeal(bytes32 _id)
	external view returns (IexecODBLibCore.Deal memory)
	{
		return m_deals[_id];
	}

	function viewTask(bytes32 _taskid)
	external view returns (IexecODBLibCore.Task memory)
	{
		return m_tasks[_taskid];
	}

	function viewContribution(bytes32 _taskid, address _worker)
	external view returns (IexecODBLibCore.Contribution memory)
	{
		return m_contributions[_taskid][_worker];
	}

	function viewScore(address _worker)
	external view returns (uint256)
	{
		return m_workerScores[_worker];
	}

	function resultFor(bytes32 id)
	external view returns (bytes memory)
	{
		IexecODBLibCore.Task storage task = m_tasks[id];
		require(task.status == IexecODBLibCore.TaskStatusEnum.COMPLETED);
		return task.results;
	}
}
