pragma solidity ^0.5.0;
pragma experimental ABIEncoderV2;

import "../node_modules/rlc-faucet-contract/contracts/RLC.sol";

import "./ERC1538/ERC1538UpgradeableStorage.sol";

import "./interfaces/ERC20.sol";
import "./libs/IexecODBLibCore.sol";
import "./libs/IexecODBLibOrders.sol";
import "./registries/RegistryBase.sol";

contract IexecStore is ERC1538UpgradeableStorage
{
	// External contracts
	IERC20       public token;
	RegistryBase public appregistry;
	RegistryBase public datasetregistry;
	RegistryBase public workerpoolregistry;

	// Escrow
	mapping(address => IexecODBLibCore.Account) internal m_accounts;

	// CategoryManager
	IexecODBLibCore.Category[] internal m_categories;

	// Hub
	bytes32 internal EIP712DOMAIN_SEPARATOR;
	bytes32 internal constant    EIP712DOMAIN_TYPEHASH    = 0x8b73c3c69bb8fe3d512ecc4cf759cc79239f7b179b0ffacaa9a75d522b39400f;
	bytes32 internal constant        APPORDER_TYPEHASH    = 0x60815a0eeec47dddf1615fe53b31d016c31444e01b9d796db365443a6445d008;
	bytes32 internal constant    DATASETORDER_TYPEHASH    = 0x6cfc932a5a3d22c4359295b9f433edff52b60703fa47690a04a83e40933dd47c;
	bytes32 internal constant WORKERPOOLORDER_TYPEHASH    = 0xaa3429fb281b34691803133d3d978a75bb77c617ed6bc9aa162b9b30920022bb;
	bytes32 internal constant    REQUESTORDER_TYPEHASH    = 0xf24e853034a3a450aba845a82914fbb564ad85accca6cf62be112a154520fae0;
	uint256 internal constant WORKERPOOL_STAKE_RATIO      = 30;
	uint256 internal constant KITTY_RATIO                 = 10;
	uint256 internal constant KITTY_MIN                   = 1000000000; // TODO: 1RLC ?
	uint256 internal constant DEADLINE_RATIO_CONTRIBUTION = 7;
	uint256 internal constant DEADLINE_RATIO_REVEAL       = 2;
	uint256 internal constant DEADLINE_RATIO_FINAL        = 10;

	mapping(bytes32 =>                    IexecODBLibCore.Task         ) internal m_tasks;
	mapping(bytes32 => mapping(address => IexecODBLibCore.Contribution)) internal m_contributions;
	mapping(address =>                    uint256                      ) internal m_workerScores;
	mapping(bytes32 => mapping(address => uint256                     )) internal m_logweight;
	mapping(bytes32 => mapping(bytes32 => uint256                     )) internal m_groupweight;
	mapping(bytes32 =>                    uint256                      ) internal m_totalweight;
	mapping(bytes32 =>                    bytes32[]                    ) internal m_requestdeals;
	mapping(bytes32 =>                    IexecODBLibCore.Deal         ) internal m_deals;
	mapping(bytes32 =>                    uint256                      ) internal m_consumed;
	mapping(bytes32 =>                    bool                         ) internal m_presigned;

	modifier onlyScheduler(bytes32 _taskid)
	{
		require(msg.sender == m_deals[m_tasks[_taskid].dealid].workerpool.owner);
		_;
	}
}
